UPDATE `blogs`.`product` SET `content` = 'Thiết kế dạng dây đeo quanh cổ mini kết hợp với cơ chế nút nhét tai giúp tai nghe nhét tai i.value BT008 phát huy tối đa thế mạnh của kết nối không dây bluetooth, từ đó hỗ trợ người dùng hiệu quả trong quá trình chạy bộ hoặc tập thể thao, tránh gây nên cảm giác vướng víu khó chịu.', `description` = 'Tai nghe ivalue BT008 được thiết kế dây đeo quanh cổ, kết hợp với cơ chế nút nhét tai hỗ trợ người dùng trong quá trình chạy bộ hoặc tập luyện, hạn chế cảm giác vướng víu và khó chịu. Đặc biệt, với khả năng kết nối không dây từ xa, giúp bạn nghe nhạc dù ở xa thiết bị 10m vẫn không bị nhiễu sóng hay gián đoạn kết nối.', `image_product` = 'https://images.fpt.shop/unsafe/fit-in/200x200/filters:quality(90):fill(white)/fptshop.com.vn/Uploads/Originals/2019/12/17/637121958918503274_HASP-Tainghe-ivalue-BT006-RED-00630786-3.jpg', `name_product` = 'Tai nghe bluetooth ivalue BT008', `price` = '225000' WHERE (`id` = '4');
UPDATE `blogs`.`product` SET `content` = 'I.value là thương hiệu sản xuất âm thanh cực kỳ đỉnh. Các dòng tai nghe hoặc loa I.value có chất lượng âm thanh rất tốt. Ưu điểm của dòng này là giá thành rẻ hơn rất nhiều so với những hãng cùng chất lượng.', `description` = 'Tai nghe nhét tai iValue M-13 là một lựa chọn lý tưởng cho những người yêu thích âm nhạc di động. Với thiết kế nhỏ gọn và ergonomically, iValue M-13 mang lại sự thoải mái khi sử dụng trong thời gian dài. Được trang bị driver động đẩy lên đến 10mm, tai nghe này cung cấp âm thanh rõ ràng, đầy đủ và cân bằng. Chất lượng âm thanh vượt trội của iValue M-13 giúp bạn tận hưởng mọi chi tiết âm nhạc một cách sâu sắc và sống động. Với cáp dài 1.2m và jack cắm 3.5mm tiêu chuẩn, bạn có thể kết nối dễ dàng với hầu hết các thiết bị âm thanh, từ điện thoại di động đến máy tính bảng và laptop.', `image_product` = 'https://images.fpt.shop/unsafe/fit-in/200x200/filters:quality(90):fill(white)/fptshop.com.vn/Uploads/Originals/2020/4/14/637224540254050984_anh-dai-dien.jpg', `name_product` = 'Tai nghe nhét tai ivalue M-13', `price` = '150000', `type_product_id` = '3' WHERE (`id` = '5');
UPDATE `blogs`.`product` SET `content` = 'Với thời lượng pin lên đến 40 giờ, Beats Solo3 sẽ trở thành chiếc tai nghe bluetooth hoàn hảo cho bạn tận hưởng những âm thanh tuyệt vời mỗi ngày. Thiết kế thông minh của sản phẩm đảm bảo nó vừa vặn với mọi kích cỡ tai và cung cấp trải nghiệm đeo thoải mái cả ngày.', `description` = 'Tổng Quan. Với thời lượng pin lên tới 40 giờ, Beats Solo3 Wireless là chiếc tai nghe hoàn hảo để đeo hàng ngày. Với công nghệ sạc nhanh Fast Fuel, bạn chỉ cần sạc 5 phút là có 3 giờ nghe. Bạn có thể thưởng thức âm thanh Beats từng đoạt giải thưởng và thoải mái nghe nhạc không dây bằng công nghệ Bluetooth loại 1.', `image_product` = 'https://images.fpt.shop/unsafe/fit-in/200x200/filters:quality(90):fill(white)/fptshop.com.vn/Uploads/Originals/2022/4/19/637859809262689444_tai-nghe-choang-dau-beats-solo3-wireless-hong-dd.jpg', `name_product` = 'Tai nghe chụp tai Beats Solo3 Wireless', `price` = '4290000', `type_product_id` = '4' WHERE (`id` = '6');





